// Filter and scan altcoins from Bybit futures
const instruments = $json.result.list;

// Filter criteria for potential altcoins
const filteredCoins = instruments.filter(instrument => {
  // Only USDT pairs
  if (!instrument.symbol.endsWith('USDT')) return false;

  // Exclude major coins and stablecoins
  const excludeList = ['BTCUSDT', 'ETHUSDT', 'USDCUSDT', 'BUSDUSDT', 'DAIUSDT', 'TUSDUSDT'];
  if (excludeList.includes(instrument.symbol)) return false;

  // Only active trading pairs
  if (instrument.status !== 'Trading') return false;

  // Must have decent volume (not delisted soon)
  return true;
});

// Sort by symbol for consistency
const sortedCoins = filteredCoins.sort((a, b) => a.symbol.localeCompare(b.symbol));

// Extract just the symbols
const symbols = sortedCoins.map(coin => coin.symbol);

return {
  json: {
    total_instruments: instruments.length,
    filtered_altcoins: symbols.length,
    all_altcoins: symbols,
    instrument_details: sortedCoins.map(coin => ({
      symbol: coin.symbol,
      status: coin.status,
      lotSizeFilter: coin.lotSizeFilter,
      priceFilter: coin.priceFilter
    }))
  }
};

// Get 24h ticker data for all altcoins to find potential watchlist candidates
const allAltcoins = $json.all_altcoins;

// Split into batches of 50 for API rate limiting
const batchSize = 50;
const batches = [];

for (let i = 0; i < allAltcoins.length; i += batchSize) {
  batches.push(allAltcoins.slice(i, i + batchSize));
}

return batches.map((batch, index) => ({ 
  json: { 
    batch_number: index + 1,
    total_batches: batches.length,
    symbols: batch 
  } 
}));

// Analyze tickers and select potential altcoins based on criteria
const tickers = $json.result.list;
const currentBatch = $('Loop Altcoin Batches').item.json.symbols;

// Filter tickers for current batch symbols
const batchTickers = tickers.filter(ticker => currentBatch.includes(ticker.symbol));

// Scoring criteria for potential altcoins
const scoredCoins = batchTickers.map(ticker => {
  const price = parseFloat(ticker.lastPrice);
  const volume24h = parseFloat(ticker.volume24h);
  const priceChange24h = parseFloat(ticker.price24hPcnt) * 100;
  const turnover24h = parseFloat(ticker.turnover24h);

  let score = 0;

  // Volume criteria (higher volume = higher score)
  if (volume24h > 1000000) score += 3;
  else if (volume24h > 500000) score += 2;
  else if (volume24h > 100000) score += 1;

  // Price movement criteria (moderate movement preferred)
  const absPriceChange = Math.abs(priceChange24h);
  if (absPriceChange > 2 && absPriceChange < 15) score += 2;
  else if (absPriceChange > 15 && absPriceChange < 30) score += 1;

  // Turnover criteria
  if (turnover24h > 10000000) score += 2;
  else if (turnover24h > 5000000) score += 1;

  // Price range criteria (avoid very cheap or very expensive coins)
  if (price > 0.01 && price < 1000) score += 1;

  return {
    symbol: ticker.symbol,
    price: price,
    volume24h: volume24h,
    priceChange24h: priceChange24h,
    turnover24h: turnover24h,
    score: score,
    ticker_data: ticker
  };
});

// Sort by score (highest first)
const sortedCoins = scoredCoins.sort((a, b) => b.score - a.score);

return {
  json: {
    batch_number: $('Loop Altcoin Batches').item.json.batch_number,
    analyzed_coins: sortedCoins.length,
    scored_coins: sortedCoins
  }
};

// Aggregate all scored altcoins from all batches
const allBatches = $input.all();
const allScoredCoins = [];

// Combine all scored coins from all batches
allBatches.forEach(batch => {
  if (batch.json.scored_coins) {
    allScoredCoins.push(...batch.json.scored_coins);
  }
});

// Sort all coins by score (highest first)
const sortedAllCoins = allScoredCoins.sort((a, b) => b.score - a.score);

// Select top performing altcoins for watchlist
const topCoins = sortedAllCoins.slice(0, 20); // Top 20 altcoins

// Create final watchlist with additional filtering
const watchlist = topCoins.filter(coin => {
  // Additional filters
  return coin.score >= 3 && // Minimum score
         coin.volume24h > 200000 && // Minimum volume
         Math.abs(coin.priceChange24h) < 50; // Avoid extreme pumps/dumps
}).slice(0, 10); // Final top 10

// Extract just the symbols for trading
const watchlistSymbols = watchlist.map(coin => coin.symbol);

// Add some stable performers if watchlist is too small
if (watchlistSymbols.length < 5) {
  const stableCoins = ['SOLUSDT', 'ADAUSDT', 'DOGEUSDT', 'MATICUSDT', 'AVAXUSDT'];
  stableCoins.forEach(coin => {
    if (!watchlistSymbols.includes(coin)) {
      watchlistSymbols.push(coin);
    }
  });
}

return {
  json: {
    total_analyzed: allScoredCoins.length,
    top_performers: topCoins.length,
    final_watchlist: watchlistSymbols.length,
    symbols: watchlistSymbols,
    watchlist_details: watchlist,
    analysis_summary: {
      avg_score: watchlist.reduce((sum, coin) => sum + coin.score, 0) / watchlist.length,
      avg_volume: watchlist.reduce((sum, coin) => sum + coin.volume24h, 0) / watchlist.length,
      avg_price_change: watchlist.reduce((sum, coin) => sum + coin.priceChange24h, 0) / watchlist.length
    }
  }
};

// Use dynamic watchlist symbols
const symbols = $json.symbols;
return symbols.map(symbol => ({ json: { symbol } }));

// Batch Analyze Multiple Tokens for Efficiency
// Get all input items from Split Watchlist Symbols
const allInputs = $input.all();

// Extract symbols from all inputs
const symbols = allInputs.map(item => item.json.symbol);

// Split into smaller batches of 3-5 tokens for ML/AI analysis
const batchSize = 3;
const batches = [];

for (let i = 0; i < symbols.length; i += batchSize) {
  batches.push(symbols.slice(i, i + batchSize));
}

return batches.map((batch, index) => ({ 
  json: { 
    batch_number: index + 1,
    total_batches: batches.length,
    symbols: batch,
    batch_size: batch.length
  } 
}));

// Batch Fetch OHLCV Data for Multiple Symbols
const batch = $('Loop Analysis Batches').item.json;
const symbols = batch.symbols;

// Fetch OHLCV data for all symbols in batch
const promises = symbols.map(async (symbol) => {
  try {
    const response = await fetch(`https://api.bybit.com/v5/market/kline?category=linear&symbol=${symbol}&interval=15&limit=30`);
    const data = await response.json();
    return {
      symbol: symbol,
      success: true,
      data: data
    };
  } catch (error) {
    return {
      symbol: symbol,
      success: false,
      error: error.message
    };
  }
});

// Wait for all requests to complete
const results = await Promise.all(promises);

return {
  json: {
    batch_number: batch.batch_number,
    total_batches: batch.total_batches,
    symbols_count: symbols.length,
    results: results
  }
};

// Enhanced Batch Technical Analysis with Multiple Candlestick Patterns
const batchData = $json;
const analysisResults = [];

// Process each symbol in the batch
for (const result of batchData.results) {
  if (!result.success || !result.data || !result.data.result) {
    console.log(`Failed to get data for ${result.symbol}:`, result.error || 'No data');
    continue;
  }

  const data = result.data.result.list;
  const symbol = result.symbol;

  // Parse OHLCV data
  const ohlcv = data.map(item => ({
    timestamp: parseInt(item[0]),
    open: parseFloat(item[1]),
    high: parseFloat(item[2]),
    low: parseFloat(item[3]),
    close: parseFloat(item[4]),
    volume: parseFloat(item[5])
  })).reverse();

  // RSI Calculation
  function calculateRSI(prices, period = 14) {
    const gains = [];
    const losses = [];

    for (let i = 1; i < prices.length; i++) {
      const change = prices[i] - prices[i - 1];
      gains.push(change > 0 ? change : 0);
      losses.push(change < 0 ? Math.abs(change) : 0);
    }

    const avgGain = gains.slice(-period).reduce((a, b) => a + b, 0) / period;
    const avgLoss = losses.slice(-period).reduce((a, b) => a + b, 0) / period;

    const rs = avgGain / avgLoss;
    return 100 - (100 / (1 + rs));
  }

  // MACD Calculation
  function calculateMACD(prices) {
    const ema12 = calculateEMA(prices, 12);
    const ema26 = calculateEMA(prices, 26);
    const macdLine = ema12 - ema26;
    const signalLine = calculateEMA([macdLine], 9);

    return {
      macd: macdLine,
      signal: signalLine,
      histogram: macdLine - signalLine
    };
  }

  function calculateEMA(prices, period) {
    const multiplier = 2 / (period + 1);
    let ema = prices[0];

    for (let i = 1; i < prices.length; i++) {
      ema = (prices[i] * multiplier) + (ema * (1 - multiplier));
    }

    return ema;
  }

  // Enhanced Candlestick Pattern Detection
  function detectCandlestickPatterns(candles) {
    const patterns = [];
    const len = candles.length;

    if (len < 3) return patterns;

    const current = candles[len - 1];
    const previous = candles[len - 2];
    const beforePrevious = candles[len - 3];

    // Helper functions
    const isBullish = (candle) => candle.close > candle.open;
    const isBearish = (candle) => candle.close < candle.open;
    const getBodySize = (candle) => Math.abs(candle.close - candle.open);
    const getUpperShadow = (candle) => candle.high - Math.max(candle.open, candle.close);
    const getLowerShadow = (candle) => Math.min(candle.open, candle.close) - candle.low;
    const getRange = (candle) => candle.high - candle.low;

    // Bullish Engulfing
    if (isBearish(previous) && isBullish(current) &&
        current.open < previous.close && current.close > previous.open) {
      patterns.push('BULLISH_ENGULFING');
    }

    // Bearish Engulfing
    if (isBullish(previous) && isBearish(current) &&
        current.open > previous.close && current.close < previous.open) {
      patterns.push('BEARISH_ENGULFING');
    }

    const currentBody = getBodySize(current);
    const currentLowerShadow = getLowerShadow(current);
    const currentUpperShadow = getUpperShadow(current);

    // Hammer
    if (currentLowerShadow > currentBody * 2 && currentUpperShadow < currentBody * 0.5) {
      patterns.push('HAMMER');
    }

    // Doji
    if (currentBody < getRange(current) * 0.1) {
      patterns.push('DOJI');
    }

    // Shooting Star
    if (currentUpperShadow > currentBody * 2 && currentLowerShadow < currentBody * 0.5) {
      patterns.push('SHOOTING_STAR');
    }

    // Morning Star Pattern
    if (len >= 3 && isBearish(beforePrevious) && 
        getBodySize(previous) < getBodySize(beforePrevious) * 0.5 &&
        isBullish(current) && current.close > (beforePrevious.open + beforePrevious.close) / 2) {
      patterns.push('MORNING_STAR');
    }

    // Evening Star Pattern
    if (len >= 3 && isBullish(beforePrevious) && 
        getBodySize(previous) < getBodySize(beforePrevious) * 0.5 &&
        isBearish(current) && current.close < (beforePrevious.open + beforePrevious.close) / 2) {
      patterns.push('EVENING_STAR');
    }

    // Marubozu (long body with minimal shadows)
    if (currentUpperShadow < currentBody * 0.1 && currentLowerShadow < currentBody * 0.1) {
      patterns.push(isBullish(current) ? 'BULLISH_MARUBOZU' : 'BEARISH_MARUBOZU');
    }

    // Spinning Top
    if (currentBody < getRange(current) * 0.3 && 
        currentUpperShadow > currentBody * 0.5 && 
        currentLowerShadow > currentBody * 0.5) {
      patterns.push('SPINNING_TOP');
    }

    return patterns.length > 0 ? patterns : ['NONE'];
  }

  // Volume Analysis
  function detectVolumeSpike(candles) {
    const avgVolume = candles.slice(-10).reduce((sum, candle) => sum + candle.volume, 0) / 10;
    const currentVolume = candles[candles.length - 1].volume;

    return {
      spike: currentVolume > avgVolume * 1.5,
      ratio: currentVolume / avgVolume,
      current: currentVolume,
      average: avgVolume
    };
  }

  // Support and Resistance Detection
  function findSupportResistance(candles) {
    const highs = candles.map(c => c.high);
    const lows = candles.map(c => c.low);

    // Find recent high and low levels
    const recentHighs = highs.slice(-10);
    const recentLows = lows.slice(-10);

    const resistance = Math.max(...recentHighs);
    const support = Math.min(...recentLows);

    return {
      resistance: resistance,
      support: support,
      current_distance_to_resistance: ((resistance - candles[candles.length - 1].close) / candles[candles.length - 1].close) * 100,
      current_distance_to_support: ((candles[candles.length - 1].close - support) / candles[candles.length - 1].close) * 100
    };
  }

  // Calculate all indicators
  const closes = ohlcv.map(item => item.close);
  const rsi = calculateRSI(closes);
  const macd = calculateMACD(closes);
  const candlestickPatterns = detectCandlestickPatterns(ohlcv);
  const volumeSpike = detectVolumeSpike(ohlcv);
  const supportResistance = findSupportResistance(ohlcv);
  const currentPrice = ohlcv[ohlcv.length - 1].close;

  // Add the analysis result to the batch results
  analysisResults.push({
    symbol: symbol,
    currentPrice: currentPrice,
    rsi: rsi,
    macd: macd,
    candlestick_patterns: candlestickPatterns,
    engulfing: {
      bullish: candlestickPatterns.includes('BULLISH_ENGULFING'),
      bearish: candlestickPatterns.includes('BEARISH_ENGULFING'),
      pattern: candlestickPatterns.includes('BULLISH_ENGULFING') ? 'BULLISH_ENGULFING' : 
               candlestickPatterns.includes('BEARISH_ENGULFING') ? 'BEARISH_ENGULFING' : 'NONE'
    },
    volumeSpike: volumeSpike,
    support_resistance: supportResistance,
    timestamp: new Date().toISOString()
  });
}

// Return the batch results
return {
  json: {
    batch_number: batchData.batch_number,
    total_batches: batchData.total_batches,
    analyzed_count: analysisResults.length,
    analysis_results: analysisResults
  }
};

// Combine sentiment analysis results from multiple models
// The merge node combines both sentiment analysis results
const mergedData = $input.all();

// Get technical analysis data
let taData = null;
try {
  taData = $('TA+Pattern+Volume').item.json;
} catch (e) {
  console.log('Could not get TA data:', e.message);
  throw new Error('Technical analysis data not found');
}

// Extract sentiment data from merged inputs
let finbertData = null;
let bertData = null;

// Process merged data to find sentiment results
for (const item of mergedData) {
  const data = item.json;

  // Check if this is a valid sentiment response
  if (data && Array.isArray(data) && data.length > 0 && data[0].label) {
    if (!finbertData) {
      finbertData = data;
    } else {
      bertData = data;
    }
  }

  // Handle error responses
  if (data && data.error) {
    console.log('Sentiment API Error:', data.error);
  }
}

// Parse FinBERT results (financial sentiment)
let finbertSentiment = { label: 'neutral', score: 0.5 };
if (finbertData && Array.isArray(finbertData) && finbertData.length > 0) {
  finbertSentiment = finbertData[0];
}

// Parse BERT results (general sentiment)
let bertSentiment = { label: 'NEUTRAL', score: 0.5 };
if (bertData && Array.isArray(bertData) && bertData.length > 0) {
  bertSentiment = bertData[0];
}

// Convert sentiment labels to standardized format
function standardizeSentiment(sentiment) {
  const label = sentiment.label.toLowerCase();
  if (label.includes('positive') || label.includes('bullish')) {
    return { label: 'POSITIVE', score: sentiment.score };
  } else if (label.includes('negative') || label.includes('bearish')) {
    return { label: 'NEGATIVE', score: sentiment.score };
  } else {
    return { label: 'NEUTRAL', score: sentiment.score };
  }
}

const standardizedFinbert = standardizeSentiment(finbertSentiment);
const standardizedBert = standardizeSentiment(bertSentiment);

// Calculate combined sentiment score
const combinedScore = (standardizedFinbert.score + standardizedBert.score) / 2;

// Determine overall sentiment
let overallSentiment = 'NEUTRAL';
if (combinedScore > 0.6) {
  overallSentiment = 'POSITIVE';
} else if (combinedScore < 0.4) {
  overallSentiment = 'NEGATIVE';
}

// Create comprehensive sentiment analysis
const sentimentAnalysis = {
  combined: {
    label: overallSentiment,
    score: combinedScore,
    confidence: Math.abs(combinedScore - 0.5) * 2 // 0-1 scale
  },
  finbert: standardizedFinbert,
  bert: standardizedBert,
  analysis: {
    agreement: standardizedFinbert.label === standardizedBert.label,
    strength: Math.abs(combinedScore - 0.5) > 0.2 ? 'STRONG' : 'WEAK'
  }
};

return {
  json: {
    symbol: taData.symbol,
    technical_analysis: {
      rsi: taData.rsi,
      macd: taData.macd,
      engulfing: taData.engulfing,
      candlestick_patterns: taData.candlestick_patterns || ['NONE'],
      volume_spike: taData.volumeSpike,
      support_resistance: taData.support_resistance,
      current_price: taData.currentPrice
    },
    sentiment: sentimentAnalysis,
    timestamp: new Date().toISOString(),
    debug: {
      merged_inputs: mergedData.length,
      finbert_available: !!finbertData,
      bert_available: !!bertData,
      ta_available: !!taData
    }
  }
};

// Parse Enhanced Groq AI Response with Advanced Sentiment
const response = $json.choices[0].message.content;
const combinedData = $('Combine Sentiment Analysis').item.json;

let aiDecision;
try {
  // Try to parse JSON response from Groq
  const jsonMatch = response.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiDecision = JSON.parse(jsonMatch[0]);
  } else {
    // Fallback parsing if JSON is not properly formatted
    aiDecision = {
      action: "HOLD",
      direction: "NONE",
      confidence: 50,
      entry_price: combinedData.technical_analysis.current_price,
      lot_size: 0.01,
      take_profit: combinedData.technical_analysis.current_price * 1.02,
      stop_loss: combinedData.technical_analysis.current_price * 0.98,
      risk_reward_ratio: 2,
      estimated_profit: 0,
      predicted_balance: 10,
      reasoning: "Unable to parse AI response, holding position"
    };
  }
} catch (error) {
  aiDecision = {
    action: "HOLD",
    direction: "NONE",
    confidence: 50,
    entry_price: combinedData.technical_analysis.current_price,
    lot_size: 0.01,
    take_profit: combinedData.technical_analysis.current_price * 1.02,
    stop_loss: combinedData.technical_analysis.current_price * 0.98,
    risk_reward_ratio: 2,
    estimated_profit: 0,
    predicted_balance: 10,
    reasoning: "Error parsing AI response: " + error.message
  };
}

// Enhanced decision with sentiment confidence weighting
if (aiDecision.confidence && combinedData.sentiment.combined.confidence) {
  // Adjust confidence based on sentiment agreement and strength
  const sentimentWeight = combinedData.sentiment.analysis.agreement ? 1.1 : 0.9;
  const strengthWeight = combinedData.sentiment.analysis.strength === 'STRONG' ? 1.05 : 0.95;

  aiDecision.confidence = Math.min(95, Math.max(5, 
    aiDecision.confidence * sentimentWeight * strengthWeight
  ));
}

return {
  json: {
    symbol: combinedData.symbol,
    timestamp: new Date().toISOString(),
    technical_analysis: combinedData.technical_analysis,
    sentiment: combinedData.sentiment,
    ai_decision: aiDecision,
    raw_groq_response: response,
    enhanced_features: {
      sentiment_models: ['FinBERT', 'BERT-Multilingual'],
      sentiment_agreement: combinedData.sentiment.analysis.agreement,
      sentiment_strength: combinedData.sentiment.analysis.strength,
      combined_confidence: Math.round(combinedData.sentiment.combined.confidence * 100)
    }
  }
};

// Generate proper Bybit API authentication headers
const crypto = require('crypto');

// Get credentials from n8n credential system
// Note: You need to configure Bybit API credentials in n8n
// For security, we'll use environment variables or credential system
const apiKey = process.env.BYBIT_API_KEY || $credentials.bybit?.apiKey || 'CONFIGURE_BYBIT_API_KEY';
const apiSecret = process.env.BYBIT_API_SECRET || $credentials.bybit?.apiSecret || 'CONFIGURE_BYBIT_API_SECRET';
const timestamp = Date.now().toString();
const recvWindow = '5000';

// Prepare the data to be signed
const orderData = {
  category: 'linear',
  symbol: $json.symbol,
  side: $json.ai_decision.action === 'BUY' ? 'Buy' : 'Sell',
  orderType: 'Market',
  qty: String($json.ai_decision.lot_size),
  timeInForce: 'IOC',
  positionIdx: 0,
  orderLinkId: `ai-bot-${$json.symbol}-${timestamp}`
};

// Create the JSON body string
const jsonBodyString = JSON.stringify(orderData);

// Create the string to be signed (for POST requests)
const stringToSign = timestamp + apiKey + recvWindow + jsonBodyString;

// Generate HMAC SHA256 signature
const signature = crypto
  .createHmac('sha256', apiSecret)
  .update(stringToSign)
  .digest('hex');

return {
  json: {
    ...orderData,
    headers: {
      'Content-Type': 'application/json',
      'X-BAPI-API-KEY': apiKey,
      'X-BAPI-TIMESTAMP': timestamp,
      'X-BAPI-RECV-WINDOW': recvWindow,
      'X-BAPI-SIGN': signature
    },
    body: jsonBodyString,
    timestamp: timestamp,
    original_data: $json
  }
};

