// Process Batch Results and Split into Individual Items for Sentiment Analysis
const batchData = $json;
const results = [];

console.log('Received batch data:', JSON.stringify(batchData, null, 2));

// Handle different data structures
let analysisResults = [];

// Check if we have analysis results from the batch
if (batchData.analysis_results && Array.isArray(batchData.analysis_results)) {
  analysisResults = batchData.analysis_results;
} else if (batchData.scored_coins && Array.isArray(batchData.scored_coins)) {
  // Handle scored coins format
  analysisResults = batchData.scored_coins.map(coin => ({
    symbol: coin.symbol,
    currentPrice: coin.price,
    rsi: 50, // Default RSI if not available
    macd: { macd: 0, signal: 0, histogram: 0 },
    candlestick_patterns: ['NONE'],
    engulfing: { bullish: false, bearish: false, pattern: 'NONE' },
    volumeSpike: { spike: false, ratio: 1, current: coin.volume24h || 0, average: coin.volume24h || 0 },
    support_resistance: {
      support: coin.price * 0.98,
      resistance: coin.price * 1.02,
      current_distance_to_resistance: 2,
      current_distance_to_support: 2
    },
    timestamp: new Date().toISOString()
  }));
} else if (Array.isArray(batchData)) {
  // Handle array format
  analysisResults = batchData;
} else {
  // Create a default analysis result from available data
  analysisResults = [{
    symbol: batchData.symbol || 'BTCUSDT',
    currentPrice: batchData.price || batchData.currentPrice || 50000,
    rsi: batchData.rsi || 50,
    macd: batchData.macd || { macd: 0, signal: 0, histogram: 0 },
    candlestick_patterns: batchData.candlestick_patterns || ['NONE'],
    engulfing: batchData.engulfing || { bullish: false, bearish: false, pattern: 'NONE' },
    volumeSpike: batchData.volumeSpike || { spike: false, ratio: 1, current: 0, average: 0 },
    support_resistance: batchData.support_resistance || {
      support: (batchData.price || 50000) * 0.98,
      resistance: (batchData.price || 50000) * 1.02,
      current_distance_to_resistance: 2,
      current_distance_to_support: 2
    },
    timestamp: new Date().toISOString()
  }];
}

// Process all tokens from batch as individual items
// This ensures each token gets proper sentiment analysis
for (const result of analysisResults) {
  if (result && (result.symbol || result.coin)) {
    const symbol = result.symbol || result.coin || 'BTCUSDT';
    const currentPrice = result.currentPrice || result.price || result.lastPrice || 50000;

    // Format each token for sentiment analysis compatibility
    results.push({
      json: {
        symbol: symbol,
        currentPrice: currentPrice,
        rsi: result.rsi || 50,
        macd: result.macd || { macd: 0, signal: 0, histogram: 0 },
        candlestick_patterns: result.candlestick_patterns || ['NONE'],
        engulfing: result.engulfing || { bullish: false, bearish: false, pattern: 'NONE' },
        volumeSpike: result.volumeSpike || { spike: false, ratio: 1, current: 0, average: 0 },
        support_resistance: result.support_resistance || {
          support: currentPrice * 0.98,
          resistance: currentPrice * 1.02,
          current_distance_to_resistance: 2,
          current_distance_to_support: 2
        },
        timestamp: result.timestamp || new Date().toISOString(),
        // Store batch info for potential use
        batch_info: {
          total_analyzed: batchData.analyzed_count || analysisResults.length,
          batch_number: batchData.batch_number || 1,
          total_batches: batchData.total_batches || 1
        }
      }
    });
  }
}

// If no results found, create a default one to prevent workflow failure
if (results.length === 0) {
  console.log('No analysis results found, creating default result');
  results.push({
    json: {
      symbol: 'BTCUSDT',
      currentPrice: 50000,
      rsi: 50,
      macd: { macd: 0, signal: 0, histogram: 0 },
      candlestick_patterns: ['NONE'],
      engulfing: { bullish: false, bearish: false, pattern: 'NONE' },
      volumeSpike: { spike: false, ratio: 1, current: 0, average: 0 },
      support_resistance: {
        support: 49000,
        resistance: 51000,
        current_distance_to_resistance: 2,
        current_distance_to_support: 2
      },
      timestamp: new Date().toISOString(),
      batch_info: {
        total_analyzed: 1,
        batch_number: 1,
        total_batches: 1
      }
    }
  });
}

console.log('Processing results:', results.length, 'items');

// Return all individual items for downstream processing
return results;
